    public class MonsterServiceTests
    {

        private MonsterService.RandomSeed Seed = new MonsterService.RandomSeed { Seed = 1234 };
        private int ExpectedRandomIndexFromSeed = 19;

        [SetUp]
        public void Setup()
        {

        }

        private Task<HttpResponseMessage> GetMonster_MockResponse(HttpRequestMessage request, CancellationToken cancellationToken)
        {
            Assert.AreEqual("/monsters/?challenge_rating=1", request.RequestUri.LocalPath);
            var response = new HttpResponseMessage(System.Net.HttpStatusCode.OK);
            response.Content = new StringContent(GetJson(), Encoding.UTF8, "application/json");
            return Task.FromResult(response);
        }

        [Test]
        public async Task GetMonster_ReturnsExpected()
        {
            var httpMessageHandlerMoq = new Mock<HttpMessageHandler>();
            httpMessageHandlerMoq.Protected()
                .Setup<Task<HttpResponseMessage>>("SendAsync", ItExpr.IsAny<HttpRequestMessage>(), ItExpr.IsAny<CancellationToken>())
                .Returns((HttpRequestMessage request, CancellationToken cancellationToken) => GetMonster_MockResponse(request, cancellationToken))
                .Verifiable();

            var subject = new MonsterService(httpMessageHandlerMoq.Object, Seed);
            var result = await subject.GetMonster();
            Assert.AreEqual("Bugbear", result.name);
        }


        private Task<HttpResponseMessage> GetMonsters_MockResponse(HttpRequestMessage request, CancellationToken cancellationToken)
        {
            Assert.AreEqual("/monsters/", request.RequestUri.LocalPath);
            var response = new HttpResponseMessage(System.Net.HttpStatusCode.OK);
            response.Content = new StringContent(GetJson(), Encoding.UTF8, "application/json");
            return Task.FromResult(response);
        }

        [Test]
        public async Task GetMonsters_ReturnsExpected()
        {
            var httpMessageHandlerMoq = new Mock<HttpMessageHandler>();
            httpMessageHandlerMoq.Protected()
                .Setup<Task<HttpResponseMessage>>("SendAsync", ItExpr.IsAny<HttpRequestMessage>(), ItExpr.IsAny<CancellationToken>())
                .Returns((HttpRequestMessage request, CancellationToken cancellationToken) => GetMonsters_MockResponse(request, cancellationToken))
                .Verifiable();

            var subject = new MonsterService(httpMessageHandlerMoq.Object, Seed);
            var actual = await subject.GetMonsters();
            var actualSerialized = JsonConvert.SerializeObject(actual);

            var expectedSerialized = JsonConvert.SerializeObject(JsonConvert.DeserializeObject<MonstersResponse>(GetJson()).results);
            Assert.AreEqual(expectedSerialized, actualSerialized);
        }


